    socialLinkReplace(tag, link, s) {
      if (link.toLowerCase().includes('twitter')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-twitter-x"></i></a>`);
      } else if (link.toLowerCase().includes('youtube')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-youtube"></i></a>`);
      } else if (link.toLowerCase().includes('facebook')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-facebook"></i></a>`);
      } else if (link.toLowerCase().includes('instagram')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-instagram"></i></a>`);
      } else if (link.toLowerCase().includes('vimeo')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-vimeo"></i></a>`);
      } else if (link.toLowerCase().includes('dribbble')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-dribbble"></i></a>`);
      } else if (link.toLowerCase().includes('behance')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-behance"></i></a>`);
      } else if (link.toLowerCase().includes('pinterest')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-pinterest"></i></a>`);
      } else if (link.toLowerCase().includes('linkedin')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-linkedin"></i></a>`);
      } else if (link.toLowerCase().includes('github')) {
        s = s.replaceAll(tag, `<a href="${link}"><i class="bi bi-github"></i></a>`);
      } else {
        s = s.replaceAll(tag, '');
      }
      return s;
    }
    changeLayout(index, clear = true) {
      let type = this.type;
      if (type === 'products-services') type = 'products'; // adjust naming for array usage
      if (type === 'call-to-action') type = 'calltoaction';
      const wrapper = document.querySelector('.is-wrapper');
      const wrap = wrapper.querySelector(`.is-me[data-type="${type}"]`);
      let html = '';
      let templateArray;
      this.layout[type] = index;
      templateArray = this.templates[type];
      html = templateArray[index].html; // html template
  
      html = this.replaceTags(html, type, templateArray[index]);
  
      //--- List Processing ---
      if (this.list[type]) {
        let templateHtml = templateArray[index].listTemplate;
        if (templateHtml) {
          const listHtml = this.getList(this.list[type], templateHtml) || '';
          html = html.replace('{%LIST%}', listHtml);
        }
        let itemTemplate = templateArray[index].itemTemplate;
        let itemEmptyTemplate = templateArray[index].itemEmptyTemplate;
        if (itemEmptyTemplate) {
          html = this.processItems(this.list[type], itemTemplate, itemEmptyTemplate, html);
        }
      }
      //--- /List Processing ---
  
      if (clear) this.textColor[type] = ['', '', '', '', '', '', '', '', ''];
      if (clear) this.overlay[type] = ['', '', '', '', '', '', '', '', ''];
  
      // Others
  
      html = html.replaceAll('[%PATH%]', this.placeholderPath);
      wrap.setAttribute('data-layout-index', index);
      let range = document.createRange();
      wrap.innerHTML = '';
      wrap.appendChild(range.createContextualFragment(html));
      this.wrapSetup(wrap);
      let boxes = wrap.querySelectorAll('.is-box');
      boxes.forEach(box => {
        this.boxSetup(box);
      });
  
      // Prevent link click
      let links = wrap.querySelectorAll('a');
      links.forEach(link => {
        link.addEventListener('click', e => {
          e.preventDefault();
          if (link.closest('[contentEditable]')) return;
          if (link.getAttribute('href').indexOf('#') === 0) {
            const elm = wrap.querySelector(link.getAttribute('href'));
            // if(elm) elm.scrollIntoView({ behavior: 'smooth', block: 'center' });
            if (elm) this.builder.scrollTo(elm);
          }
        });
      });
      let divSocial = wrap.querySelectorAll('.is-social');
      divSocial.forEach(div => {
        div.addEventListener('click', () => {
          this.settings.open();
        });
      });
      let btns = wrap.querySelectorAll('a[role="button"]');
      btns.forEach(btn => {
        btn.addEventListener('click', () => {
          this.settings.open();
        });
      });
  
      // Save
      localStorage.setItem('_layouts', JSON.stringify(this.layout));
      localStorage.setItem('_textcolor', JSON.stringify(this.textColor));
      localStorage.setItem('_overlay', JSON.stringify(this.overlay));
  
      // Render Typography
  
      this.builder.util.renderTypography(wrap);
  
      // Render text color & overlay
  
      this.builder.util.renderOverlayAndTextColor(wrap);
  
      // Render images
  
      this.builder.util.renderImages(wrap);
      this.builder.refreshButtons();
    }
