    async generate(userInfo) {
      this.generating = true;
      this.tokenInput = 0;
      this.tokenOutput = 0;
      const name = userInfo.name;
      const fields = userInfo.fields;
      const about = userInfo.about;
      const type = userInfo.type;
      const style = userInfo.style;
      const instructions = userInfo.instructions;
      if (this.onProgress) this.onProgress(this.out('Exploring Your Vision...'), false);
  
      /*
      Focusing. This returns:
      1. list of products/services based on the information provided
      2. Common terms
      */
      let focusing = await this.focusing.getTerms(userInfo);
      if (!focusing) return false; //aborted
  
      let terms = focusing.common_terms;
      if (this.onProgress) this.onProgress(this.out('Generating Creative Ideas...'), false);
      setTimeout(() => {
        if (this.onProgress) this.onProgress(this.out('Ideas Taking Shape...'), false);
      }, 4000);
      let info = await this.clientInfo.getInfo({
        name,
        type,
        fields,
        about,
        style,
        terms,
        instructions
      });
      if (!info) return false; //aborted
  
      // Adding products/services info
      info = info + `
          
  Products/Services:
  
  ${focusing.products_or_services}
          `;
  
      // Complete information that will be used for writing a copy.
      this.baseInfo = {
        name,
        type,
        fields,
        about,
        style,
        terms,
        info,
        instructions
      };
      setTimeout(() => {
        if (this.onProgress) this.onProgress(this.out('Crafting Compelling Content...'), false);
        setTimeout(() => {
          if (this.onProgress) this.onProgress(this.out('Almost Finished...'), false);
        }, 6000);
      }, 6000);
      let args = await this.webCopy.write(this.baseInfo);
      if (!args) return false; //aborted
  
      if (this.onProgress) this.onProgress(this.out('Get Ready...'), false);
      let argsExtra = await this.webExtra.write(this.baseInfo);
      if (!argsExtra) return false; //aborted
  
      args = {
        ...args,
        ...argsExtra
      };
      localStorage.setItem('_webcopy', JSON.stringify(args));
      localStorage.setItem('_baseinfo', JSON.stringify(this.baseInfo)); //save baseInfo after all generated
  
      this.args = args; // a must
  
      // clear
      localStorage.removeItem('_sections');
      localStorage.removeItem('_layouts');
      localStorage.removeItem('_textcolor');
      localStorage.removeItem('_images');
      localStorage.removeItem('_overlay');
      localStorage.removeItem('_typography');
      this.sections = this.defaultSections; // set to default after generating
  
      this.content.imagePanel.images = {};
      this.content.textColor = {};
      this.content.overlay = {};
      this.content.typography = {
        className: 'type-system-ui',
        contentCss: 'type-system-ui.css'
      };
      if (this.consoleLog) console.log(args);
  
      // if(this.onProgress) this.onProgress(this.out('Building Your Website...'), false);
      if (this.onProgress) this.onProgress(this.out('Your Website is Ready!'), true);
      this.generating = false;
      const cost = this.calculateCost();
      if (this.consoleLog) console.log(`$${cost}`);
      return true;
    }
    calculateCost() {
      const inputCost = 0.50 / 1000000;
      const outputCost = 1.50 / 1000000;
      const inputCostTotal = this.tokenInput * inputCost;
      const outputCostTotal = this.tokenOutput * outputCost;
      const totalCost = inputCostTotal + outputCostTotal;
      return totalCost.toFixed(3);
    }
