    async send(mode, question, context, system, functs) {
      this.controller = new AbortController();
      this.signal = this.controller.signal;
      let temperature = 0.6;
      let topP = 0.9;
      let num = 1;
      const messages = {
        question,
        context,
        system,
        functs,
        temperature,
        topP,
        num
      };
      try {
        let data;
        let headers = {
          ...this.headers,
          ...this.defaultHeaders
        };
        const response = await fetch(this.sendCommandUrl, {
          signal: this.signal,
          // Abort
          method: 'POST',
          headers,
          body: JSON.stringify(messages)
        });
        data = await response.json();
        if (data.error) {
          // Error response from OpenAI goes here.
          console.log('Error:\n' + data.error);
  
          // ex. wrong API Key => Request failed with status code 401
          // this.showMessage(out('Request failed.'));
  
          this.finish();
          return false;
        }
        if (data.usage) {
          this.tokenInput += data.usage.prompt_tokens;
          this.tokenOutput += data.usage.completion_tokens;
        }
        if (mode === 'assistant') {
          let answer;
          data.answer.choices.forEach(item => {
            answer = item.message.content;
          });
          return answer;
        } else {
          return data.answer;
        }
      } catch (error) {
        if (error.name === 'AbortError') {
          // Do Nothing
          console.log('Request aborted by user.');
        } else {
          // CORS or code errors goes here
          console.error('Error:', error);
          // console.log('Error:\n'+error);
  
          this.finish();
  
          // this.showMessage(out('Service Unavailable.'));
        }
  
        return false;
      }
    }
