    sectionCss() {
      let contentStylePath = this.contentStylePath;
      let css = `<link data-name="contentstyle" data-class="type-system-ui" href="${contentStylePath}type-system-ui.css" rel="stylesheet">`;
      if (localStorage.getItem('_typography')) {
        let typography = JSON.parse(localStorage.getItem('_typography'));
        let className = typography.className;
        let contentCss = typography.contentCss;
        css = `<link data-name="contentstyle" data-class="${className}" href="${contentStylePath + contentCss}" rel="stylesheet">`;
      }
      return css;
    }
    download(options = {
      //     replace: [
      //         ['assets/placeholders/','http://localhost:8085/assets/placeholders/']
      //      ]
    }) {
      if (this.demoMode) {
        alert(this.out('Download feature is disabled in this demo.'));
        return;
      }
      let urlBase = window.location.href.substring(0, window.location.href.lastIndexOf('/'));
      let pageTemplate = this.pageTemplate ? this.pageTemplate : this.getPageTemplate();
  
      // Add required typography css for the content
      let contentStylePath = this.contentStylePath;
      contentStylePath = contentStylePath.startsWith('/') === 0 ? urlBase + contentStylePath : urlBase + '/' + contentStylePath;
      let css = `
  <link data-name="contentstyle" data-class="type-system-ui" href="${contentStylePath}type-system-ui.css" rel="stylesheet">
  `;
      if (localStorage.getItem('_typography')) {
        let typography = JSON.parse(localStorage.getItem('_typography'));
        let className = typography.className;
        let contentCss = typography.contentCss;
        css = '<link data-name="contentstyle" data-class="' + className + '" href="' + contentStylePath + contentCss + '" rel="stylesheet">';
      }
  
      // Typography css
      pageTemplate = pageTemplate.replaceAll('[%SECTIONCSS%]', css);
  
      // [%PATH%]
      if (options.path) {
        pageTemplate = pageTemplate.replaceAll('[%PATH%]', options.path);
      } else {
        pageTemplate = pageTemplate.replaceAll('[%PATH%]', urlBase);
      }
      let s = localStorage.getItem('_webcopy');
      const args = JSON.parse(s);
      let html = this.content.getHtml(args, true, true); // forExport=true, forDownload=true
  
      // Custom
      if (options.replace) {
        options.replace.forEach(rule => {
          html = html.replaceAll(`${rule[0]}`, rule[1]);
        });
      } else {
        html = html.replaceAll('assets/placeholders/', urlBase + '/assets/placeholders/'); // if there is still images that use placeholders
      }
      // Beautify
      // html = this.beautify(html);
  
      html = pageTemplate.replace('[%CONTENT%]', html);
      const a = document.createElement('a');
      a.href = window.URL.createObjectURL(new Blob([html], {
        type: 'text/plain'
      }));
      a.download = 'page.html';
      a.click();
    }
