    changeTypography() {
      let randomIndex = Math.floor(Math.random() * this.typographyPanel.contentStyles.data.styles.length);
      let item = this.typographyPanel.contentStyles.data.styles[randomIndex];
      const css = {
        className: item.classname,
        contentCss: item.sectioncss
      };
      localStorage.setItem('_typography', JSON.stringify(css)); //save
  
      let s = localStorage.getItem('_webcopy');
      const args = JSON.parse(s);
      let html = this.content.getHtml(args);
      this.content.render(html);
    }
    changeLayout() {
      localStorage.removeItem('_layouts');
      let s = localStorage.getItem('_webcopy');
      const args = JSON.parse(s);
      let html = this.content.getHtml(args);
      this.content.render(html);
    }
    clearContent() {
      // Clear content
      localStorage.removeItem('_webcopy');
      localStorage.removeItem('_sections');
      localStorage.removeItem('_baseinfo');
      let element = this.element;
      const panelGenerate = element.querySelector('.panel-generate'); // generate panel
      const panelContent = element.querySelector('.panel-content'); // result (generated content) panel
      const btnGotoDasboard = panelGenerate.querySelector('.btn-dashboard');
      panelGenerate.classList.add('active');
      panelContent.classList.remove('active');
      btnGotoDasboard.classList.add('hidden');
    }
    clear() {
      // Clear content
  
      let element = this.element;
      localStorage.removeItem('_webcopy');
      localStorage.removeItem('_baseinfo');
      localStorage.removeItem('_sections');
      localStorage.removeItem('_layouts');
      localStorage.removeItem('_textcolor');
      localStorage.removeItem('_images');
      localStorage.removeItem('_overlay');
      localStorage.removeItem('_typography');
      localStorage.removeItem('_moduledata');
      let currentLink = document.querySelector('link[data-name="contentstyle"]');
      if (currentLink) currentLink.remove();
      if (!this.customForm) {
        const panelGenerate = element.querySelector('.panel-generate');
        const btnGotoDasboard = panelGenerate.querySelector('.btn-dashboard');
        panelGenerate.classList.add('active');
        btnGotoDasboard.classList.add('hidden');
      }
      const panelContent = element.querySelector('.panel-content');
      panelContent.classList.remove('active');
      if (this.onClear) this.onClear();
    }
    destroy() {
      const element = this.element;
      window.removeEventListener('scroll', this.scrollHandle);
      const panelGenerate = element.querySelector('.panel-generate');
      const panelContent = element.querySelector('.panel-content');
      const panelTypography = element.querySelector('.typography-panel');
      const panelLayout = element.querySelector('.layout-panel');
      const panelImage = element.querySelector('.image-panel');
      if (panelGenerate) panelGenerate.remove();
      if (panelContent) panelContent.remove();
      if (panelTypography) panelTypography.remove();
      if (panelLayout) panelLayout.remove();
      if (panelImage) panelImage.remove();
    }
    out(s) {
      let val = this.lang[s];
      if (val) return val;else {
        return s;
      }
    }
